body {
  height: 100vh;
  /*background-image: url("../img/bg.png");*/
  background-size: cover;
  background-position: center;
  font-size: 62.5%;
}


#dnd-area {
  width: 600px;
  height: 300px;
  margin: 0 auto 40px;
  background-color: #b2ebf2;
}

#original-image-label {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100%;
  position: relative;
  color: #1a237e;
}

#original-image-label:before {
  content: '';
  display: block;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  margin: 20px;
  border: 4px dashed #1a237e;
}

#original-image-file {
  display: none;
}

#thumbnail-area {
  width: 600px;
  margin: 0 auto;
}

.cropper-area {
  display: flex;
  width: 100%;
  margin: 0 auto;

  & .cropper-image {
    flex: 0 0 50%;
  }

  & .cropper-preview {
    flex: 0 0 50%;
  }
}

.preview-area {
  width: 50%;
  height: 50vw;
  margin: 0 auto;
  overflow: hidden;
}
